    res.status(200).json({
      is_confirmed: user.is_confirmed,
      is_expired: isExpired,
    });
  } catch (error) {
    console.error(' Error verificando estado del token:', error);
    res.status(500).json({ message: 'Error en el servidor' });
  }
};

